const readLineSync = require("readLine-sync")

const num1 = readLineSync.question("please eneter first number: ")
const num2 = readLineSync.question("please enter second number: ")
const operation = ["add", "subtract", "divide", "multiply"]
const choose = readLineSync.keyInSelect(operation, "choose operation")

if (choose === 0) {
    console.log(+num1 + +num2)
}
if (choose === 1) {
    console.log(+num1 - +num2)
}
if (choose === 2) {
    console.log(+num1 / +num2)
}
if  (choose === 3) {
    console.log(+num1 * +num2)
}